import React, { Component } from 'react'

export default class UpdateForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            categoryName: '',
            productName: '',
            newPrice: '',
            newStock: ''
        };
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        // Kategori veya ürün güncelleme işlemleri burada yapılacak
        if (this.props.updateType === 'category') {
            console.log('Kategori Güncellendi:', this.state.categoryName);
        } else if (this.props.updateType === 'product') {
            console.log('Ürün Güncellendi:', this.state.productName, this.state.newPrice, this.state.newStock);
        }
        this.setState({
            categoryName: '',
            productName: '',
            newPrice: '',
            newStock: ''
        });
    };

    render() {
        return (
            <div>
                {this.props.updateType === 'category' ? (
                    <div>
                        <h2>Kategori Güncelle</h2>
                        <form onSubmit={this.handleSubmit}>
                            <input
                                type="text"
                                placeholder="Yeni Kategori Adı"
                                name="categoryName"
                                value={this.state.categoryName}
                                onChange={this.handleChange}
                            />
                            <button type="submit">Güncelle</button>
                        </form>
                    </div>
                ) : (
                    <div>
                        <h2>Ürün Güncelle</h2>
                        <form onSubmit={this.handleSubmit}>
                            <input
                                type="text"
                                placeholder="Ürün Adı"
                                name="productName"
                                value={this.state.productName}
                                onChange={this.handleChange}
                            />
                            <input
                                type="text"
                                placeholder="Yeni Fiyat"
                                name="newPrice"
                                value={this.state.newPrice}
                                onChange={this.handleChange}
                            />
                            <input
                                type="text"
                                placeholder="Yeni Stok Miktarı"
                                name="newStock"
                                value={this.state.newStock}
                                onChange={this.handleChange}
                            />
                            <button type="submit">Güncelle</button>
                        </form>
                    </div>
                )}
            </div>
        )
    }
}
