import React, { Component } from 'react';
import UpdateForm from './UpdateForm'; // UpdateForm bileşenini dahil ediyoruz

export default class Body extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showUpdateForm: false,
            updateType: null
        };
    }

    handleUpdateClick = (type) => {
        this.setState({
            showUpdateForm: true,
            updateType: type
        });
    };

    render() {
        return (
            <div style={{ margin: '5%' }}>
                <h1>Admin Paneli</h1>
                <h2>Category List</h2>
                <div>
                    <h4 style={{ display: "inline-block" }}>Add New Category :</h4>
                    <button className="button" role="button">Add Category</button>
                </div>

                <table>
                    {/* Kategoriler daha sonra veritabanından çekilecek*/}
                    <tr>
                        <th><h3>Category Id</h3></th>
                        <th><h3>Category Name</h3></th>
                        <th><h3>Update Category</h3></th>
                        <th><h3>Delete Category</h3></th>
                    </tr>
                    <tr>
                        <td>1</td>
                        <td>T-shirt</td>
                        <td><button className="button" role="button" onClick={() => this.handleUpdateClick('category')}>Update</button></td>
                        <td><button className="buttonDelete" role="button">Delete</button></td>
                    </tr>
                    <tr>
                        <td>2</td>
                        <td>Pants</td>
                        <td><button className="button" role="button" onClick={() => this.handleUpdateClick('category')}>Update</button></td>
                        <td><button className="buttonDelete" role="button">Delete</button></td>
                    </tr>
                </table>

                {/* Güncelleme formunu göstermek için */}
                {this.state.showUpdateForm && this.state.updateType === 'category' && <UpdateForm />}
            </div>
        )
    }
}
