import React, { useState, useEffect } from 'react';
import Navbar from './Navbar'
import Product from './Products';
import Customer from './Customers';
import '../../Css/admin.css';

function Header() {
    const [content, setContent] = useState('HOME');

    useEffect(() => {
        // Feather ikonlarını yüklemek için
        const feather = require('feather-icons');
        feather.replace();
    }, []);

    const showProduct = () => {
        setContent(<Product />);
    };

    const showCustomer = () => {
        setContent(<Customer />);
    };

    return (
        <div className="header2">
            <div className="header__logo">
                <strong>KAFT</strong>
            </div>
            <nav className="navbar2">
                <ul className="navbar__menu2">
                    <li className="navbar__item2">
                        <a href="#" className="navbar__link2">
                            <i data-feather="home"></i>
                            <span>Home</span>
                        </a>
                    </li>
                    <li className="navbar__item2">
                        <a href="#" className="navbar__link2" onClick={showProduct}>
                            <i data-feather="message-square"></i>
                            <span>Products</span>
                        </a>
                    </li>
                    <li className="navbar__item2">
                        <a href="#" className="navbar__link2" onClick={showCustomer}>
                            <i data-feather="users"></i>
                            <span>Customers</span>
                        </a>
                    </li>
                    <li className="navbar__item2">
                        <a href="#" className="navbar__link2">
                            <i data-feather="folder"></i>
                            <span>Categories</span>
                        </a>
                    </li>
                </ul>
            </nav>
            <div>
                <div>{content}</div>
            </div>
        </div>
    );
}

export default Header;
