// LoginModal.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../../Css/Login.css'
import '../HomePage/Login.js';



function LoginModal({ onClose }) {
  const CreateUser = () => {
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [password, setPassword] = useState('');
    const [eposta, setEmail] = useState('');
    const [dateofregistiration, setDate] = useState('');
    const [adressID, setAdressID] = useState('');

    const handleSubmit = async (event) => {
      event.preventDefault();
      try {
        const userData = { name, surname, password, eposta, dateofregistiration, adressID };
        await userService.createUser(userData);
        alert('User created successfully');
        // Başarılı işlem sonrası yapılacaklar (formu temizleme, listeyi yenileme vb.)
      } catch (error) {
        console.error('Error creating user:', error);
        alert('Error creating user');
      }
    };
  }

    return (
      <div className="login-modal">
        <div className="login-modal-content">
          <span className="close" onClick={onClose}>&times;</span>

          <h3 style={{ color: 'black', float: 'left', fontFamily: 'Font1', marginTop: '-25px' }}>Üyelik</h3>
          <br></br>

          {/* Giriş yap formu */}

          <form onSubmit={handleSubmit}>

            {/* Kullanıcı adı */}
            <label htmlFor="username" className="labelgiriş">E-posta</label>
            <input type="text" id="username" name="username" className="input" />
            <input type="text" value={name} onChange={(e) => setName(e.target.value)} />

            {/* Şifre */}
            <label htmlFor="password" className="labelgiriş">Şifre</label>
            <input type="password" id="password" name="password" className="input" />


            {/* "Beni Hatırla" metni */}
            <input type="checkbox" id="rememberMe" className="irememberMe" />
            <label htmlFor="rememberMe" className="irememberLabel">Beni Hatırla</label>

            <a href="#" className="şifreyiunuttum">Şifremi Unuttum</a>
            <input type="submit" value="Giriş Yap" className="login-button" />

          </form>
          <br />

          <div className="button-container">
            {/* Facebook ile giriş */}
            <div className="square-button">
              <button>Facebook ile Giriş</button>
            </div>
            {/* Google ile giriş */}
            <div className="square-button">
              <button>Google ile Giriş</button>
            </div>
            {/* Üye ol */}
            <div className="square-button">
              <Link to="/login">üye ol</Link>
            </div>
          </div>
        </div>
        <div className="login-modal-background" onClick={onClose} />
      </div>
    );
  }

  export default LoginModal;
