import React, { Component } from 'react';

export default class UpdateCategory extends Component {
    constructor(props) {
        super(props);
        this.state = {
            newCategoryName: ''
        };
    }

    handleChange = (e) => {
        this.setState({
            newCategoryName: e.target.value
        });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        // Kategori güncelleme işlemleri burada yapılacak
        console.log('Kategori Güncellendi:', this.state.newCategoryName);
        console.log('Category Id:', this.props.categoryId); // Category id değerini konsola yazdırıyoruz
        this.setState({
            newCategoryName: ''
        });
    };

    render() {
        return (
            <div>
                <h2>Update Category</h2>
                {/* Kategori güncelleme formu buraya gelecek */}
                <form onSubmit={this.handleSubmit}>
                    <input
                        type="text"
                        placeholder="Yeni Kategori Adı"
                        value={this.state.newCategoryName}
                        onChange={this.handleChange}
                    />
                    <button type="submit">Güncelle</button>
                </form>
            </div>
        )
    }
}
