import React, { Component } from 'react';

export default class UpdateForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showProductFields: false,
            showCategoryFields: false
        };
    }

    handleProductClick = () => {
        this.setState({
            showProductFields: true,
            showCategoryFields: false
        });
    };

    handleCategoryClick = () => {
        this.setState({
            showProductFields: false,
            showCategoryFields: true
        });
    };

    render() {
        return (
            <div>
                <button onClick={this.handleProductClick}>Ürün Güncelle</button>
                <button onClick={this.handleCategoryClick}>Kategori Güncelle</button>
                {this.state.showProductFields && (
                    <div>
                        <input type="text" placeholder="Ürün adı" />
                        <input type="text" placeholder="Ürün fiyatı" />
                        <input type="text" placeholder="Ürün stoku" />
                        <input type="text" placeholder="Ürün açıklaması" />
                        <button>Ürünü Güncelle</button>
                    </div>
                )}
                {this.state.showCategoryFields && (
                    <div>
                        <input type="text" placeholder="Kategori adı" />
                        <input type="text" placeholder="Kategori açıklaması" />
                        <button>Kategoriyi Güncelle</button>
                    </div>
                )}
            </div>
        );
    }
}
